import React, { useState } from 'react';
import { Link } from 'react-router-dom';

import { AiOutlineMenu } from 'react-icons/ai';

function Navbar({ path }) {
  const [click, setClick] = useState(false);

  const handleClick = () => setClick(!click);
  const closeMobileMenu = () => setClick(false);

  const storeScroll = () => {
    document.documentElement.dataset.scroll = window.scrollY;
  };

  const outOfHome = path !== '/'

  const navbarStyle = outOfHome ? {
    backgroundColor: '#252525',
    color: 'black',
    boxShadow: '0 0 .5em rgba(0, 0, 0, .5)'
  } : {}

  // Listen for new scroll events
  document.addEventListener('scroll', storeScroll);

  // Update scroll position for first time
  storeScroll();


  return (
    <>
      <nav>
        <Link to='/' className='navbar-logo'>
          <img className='title-logo'
            alt='Compa logo'
            src='/images/compa negro.png'
          />
        </Link>

        <ul>
          <li>
            <Link to='/agro' > Agro </Link>
          </li>
          <li>
            <Link to='/tech' > Tecnología </Link>
          </li>
          <li>
            <Link to='/edu' > Educación </Link>
          </li>
          <li>
            <Link to='/mercado' > Mercado </Link>
          </li>
        </ul>
        <AiOutlineMenu />
      </nav>
    </>
  );
}

export default Navbar;
